////////////////////////////////////////////////////////////////
// 土地相关
////////////////////////////////////////////////////////////////
// 土地的状态标识
declare enum LandState {
    Nothing = 0,
    Virgin = 1,
    Dry = 2,
    DryHalf = 3,
    FatHalf = 4,
    Fat = 5,
}


// 默认初始化作物的行数
declare const YCountOfCrops = 2;

////////////////////////////////////////////////////////////////



////////////////////////////////////////////////////////////////
// 作物相关
////////////////////////////////////////////////////////////////

// 声明全局的枚举类型：作物状态
declare enum CropState {
    Seed = 0,
    Seeding = 1,
    Growing = 2,
    Flowering = 3,
    Fructifying = 4,
    Harvest = 1000,
}

// 声明全局的枚举类型：作物Id
declare enum CropIdRange {
    Low = 101,      // 种子Id起始序号
    High = 115,     // 种子Id结束序号
}

// 作物配置文件名
declare const CropDataResourceName = "crops";   //resources/crops.json


// 声明一个json数据
declare interface CropData {
    id: number;
    name: string;
    state: CropState;
    spriteFrameName: string;
}
