  #if CC_USE_DEBUG_VIEW == CC_SURFACES_DEBUG_VIEW_COMPOSITE_AND_MISC && CC_SURFACES_ENABLE_DEBUG_VIEW
    if (!IS_DEBUG_VIEW_COMPOSITE_ENABLE_GAMMA_CORRECTION) {
      return linear;
    }
  #endif
#endif
  return sqrt(linear);
}
vec3 ACESToneMap (vec3 color) {
  color = min(color, vec3(8.0));
  const float A = 2.51;
  const float B = 0.03;
  const float C = 2.43;
  const float D = 0.59;
  const float E = 0.14;
  return (color * (A * color + B)) / (color * (C * color + D) + E);
}
varying vec2 v_uv;
varying vec4 v_offset;
uniform sampler2D u_texSampler;
uniform sampler2D u_blendTexSampler;
vec3 ToLDR(vec3 color) {
  #if CC_USE_HDR
    color *= cc_exposure.x * FP_SCALE_INV;
    color = ACESToneMap(color);
    color = LinearToSRGB(color);
  #endif
  return color;
}
vec4 frag () {
