    colorBlurred.rgb += kernel[i].rgb * mix(colorM.rgb, color.rgb, SSSS_STRENGTH_SOURCE_INLOOP);
  }
  #if !CC_USE_HDR
     colorBlurred = vec4(SRGBToLinear(colorBlurred.rgb), colorBlurred.a);
  #endif
  return colorBlurred;
}
varying vec2 v_uv;
void main() {
  gl_FragColor = vec4(blur(v_uv, vec2(0.0, 1.0)).rgb, 1.0);
}
